formulas.expression:
IF(
  ISBLANK({!varRespAttyEmail}),
  '',                       /* nothing */
  {!varRespAttyEmail}
) &
IF(
  AND(
      NOT(ISBLANK({!varRespAttyEmail})),
      NOT(ISBLANK({!varRespParaEmail}))
  ),
  ';',                      /* separator only if both exist */
  ''
) &
IF(
  ISBLANK({!varRespParaEmail}),
  '',
  {!varRespParaEmail}
)
